import { useState, useEffect } from 'react';

import { useBaseUrl } from '../../utils/providers/urlprovider';
import { getActiveClassroom, setNewActiveClassroom } from '../../services/admin/classroom';

import Sidebar from './SidebarAdmin';
import Mainview from './Mainview';
import FirstClassroom from './FirstClassroom';

import '../../styles/admin/views.css';
import '../../styles/admin/sidebar.css';
import '../../styles/admin/navbar.css';


const AdminHome = ({handleAdminLogout}) => {
    const [ classroomExists, setClasroomExists ] = useState(false);
    const [ activeClassroom, setActiveClassroom ] = useState({});
    const [ students, setStudents ] = useState([]);
    const [ todoView, setTodoView ] = useState(true);

    const toggleView = () => {setTodoView(!todoView)}

    const baseURL = useBaseUrl();
    const token = window.localStorage.getItem("token");
    const username = window.localStorage.getItem("username");
    const isAdmin = window.localStorage.getItem("admin");


    const fetchActiveClassroom = async () => {
        try {
            const classroom = await getActiveClassroom(baseURL, token, username);
            if(classroom) {
                changeActiveClassroom(classroom);

            } else {
                setActiveClassroom({});
                setClasroomExists(false);
            }
          } catch (error) {
            console.error('Error fetching active classroom:', error);
          }
    }

    const handleClassroomClick = async (classroomName) => {
        try {
          await setNewActiveClassroom(baseURL, token, username, classroomName);

        } catch (error) {
            console.error('Error updating active classroom:', error);
        }
    };


    const changeActiveClassroom = (classroom) => {
        if (!classroom) {
            setActiveClassroom({});
            setClasroomExists(false);
        } else {
            setClasroomExists(true);
            setActiveClassroom(classroom);
        }
    }


    useEffect(() => {
        fetchActiveClassroom();
    }, [activeClassroom]);



    return (
        <div className='home-container full-container'>
            {!classroomExists ? (
                <div className='flex-center-column full-container'>
                    <FirstClassroom setActiveClassroom={changeActiveClassroom}/>
                </div>
            ): (
                <div className='full-container home-container'>
                    <Mainview todoView={todoView} activeClassroom={activeClassroom}/>
                    <Sidebar toggleView={toggleView} 
                            todoView={todoView} 
                            handleAdminLogout={handleAdminLogout}
                            activeClassroom={activeClassroom}
                            setActiveClassroom={changeActiveClassroom}/>
                </div>
            )}
        </div>
    );
}

export default AdminHome;
