import { useState, useCallback, useEffect } from "react";

import { useBaseUrl } from "../../utils/providers/urlprovider";

import { getTodos } from "../../services/admin/todos";
import { formattedDate } from "../../utils/helperFunctions";

const TodoCard = ({cardItem}) => {
    const [todos, setTodos] = useState([]);

    const baseURL = useBaseUrl();
    const token = window.localStorage.getItem("token");

    const getAllTodos = useCallback(async () => {
      try {
        const result = await getTodos(baseURL, token, cardItem.class_id);
        setTodos(result);
      } catch (error) {
        console.error('Error fetching todos:', error);
      }
    }, [baseURL, token, cardItem.class_id]);

    useEffect(() => {
      const fetchClasses = async () => {
        await getAllTodos();
      };

      fetchClasses();
    }, [getAllTodos]);



    return (
        <div className="full-container todo-card">
            <div className="card-header">
                <h2 className="second-header">{cardItem.name}</h2>
                <p>{formattedDate(cardItem.date_created)}</p>

            </div>
            <div className="card-body">


            </div>
            <div className="card-buttons">
                <TodoCardOption type={"add"}/>
                <TodoCardOption type={"share"}/>
                <TodoCardOption type={"trash"} />
            </div>
        </div>
    )
}


const TodoCardOption = ({type}) =>  {
    return (
        <div className="todo-card-option full-container flex-center">
            {type === "trash" ?
                <ion-icon name="trash"></ion-icon>
                :
                type === "add" ? 
                <ion-icon name="add-outline"></ion-icon>
                :
                <ion-icon name="desktop"></ion-icon>
            }
        </div>
    );
}

export default TodoCard;
