import { useState } from 'react'
import '../../styles/login/login.css'


const ChooseLoginButton = ({name, admin=false}) => {
    const [isAdmin] = useState(admin);

    return (
        <div className={`choose-login-button flex-center ${isAdmin ? 'primary-color' : 'secondary-color'}`}>
            <h1 className='header'>{name}</h1>
        </div>
    )
}

const LeftContainer = () => {
    return (
        <div className='half-login-container left-login-container relative'>
            <ChooseLoginButton name="Member" />
        </div>
    )
}

const RightContainer = () => {
    return (
        <div className='half-login-container right-login-container relative'>
            <ChooseLoginButton name="Admin" />
        </div>
    )
}

const ChooseLogin = () => {
    return (
        <div className='full-container login-container'>
            <LeftContainer />
            <RightContainer />
        </div>
    )

}

export default ChooseLogin
