import React, { useState, useCallback, useEffect, useRef } from "react";

import { useBaseUrl } from "../../utils/providers/urlprovider";
import { useActiveClassroom } from "../../utils/providers/ActiveClassroomContext";
import { useShareContext } from "../../utils/providers/ShareContext";

import { getTodos, updateTodoComplete, updateTodoContent, updateTodoFlag, deleteTodo, addTodo, updatePosition } from "../../services/admin/todos";
import { getAnswer, handleAnswer } from "../../services/admin/answers";
import { getSharedClass, setSharedClass, deleteClass } from "../../services/admin/class";
import { formattedDate } from "../../utils/helperFunctions";



const TodoCard = ({ cardItem, getAllClasses, shareView = false }) => {
    const [todos, setTodos] = useState([]);
    const [addingTodo, setAddingTodo] = useState(false);
    const [dragOverItem, setDragOverItem] = useState(null);

    const changeAddingStatus = () => {
      setAddingTodo(!addingTodo);
    };

    const baseURL = useBaseUrl();
    const token = window.localStorage.getItem("token");

    const getAllTodos = useCallback(async () => {
      try {
        const result = await getTodos(baseURL, token, cardItem.class_id);
        setTodos(result);
      } catch (error) {
        console.error("Error fetching todos:", error);
      }
    },[baseURL, token, cardItem.class_id]);

    const firstTodo = useRef(null);
    const secondTodo = useRef(null);
    const sortedTodos = [...todos].sort((a, b) => a.position - b.position);

    const handleDragEnd = async () => {

        console.log(todos);
        console.log("position updated");

        console.log(firstTodo.current, secondTodo.current);
        const todo1 = sortedTodos[firstTodo.current];
        const todo2 = sortedTodos[secondTodo.current];
        console.log(todo1.content, todo2.content);
        await updatePosition(baseURL, token, todo1, todo2);
        getAllTodos();
        console.log(todos);
        firstTodo.current = null;
        secondTodo.current = null;
        setDragOverItem(null); 
    };

    useEffect(() => {
      getAllTodos();
    }, [cardItem.class_id, getAllTodos]);

    return (
      <div className="full-container todo-card resize">
        <div className="card-header">
          <h2 className="second-header scroll">{cardItem.name}</h2>
          <p>{formattedDate(cardItem.date_created)}</p>
        </div>
        <div className="card-body scroll">
          {todos &&
            sortedTodos.map((todo, index) => (
                <div
                    key={todo.todo_id}
                    className={`draggable ${dragOverItem === todo ? "drag-over" : ""}`}
                    draggable="true"
                    onDragStart={() => (firstTodo.current = index)}
                    onDragEnter={() => (secondTodo.current = index)}
                    onDragOver={(e) => {
                    e.preventDefault();
                    setDragOverItem(todo);
                    }}
                    onDragLeave={() => setDragOverItem(null)}
                    onDragEnd={handleDragEnd}
                >
                    <SingleTodo todo={todo} onUpdate={getAllTodos} />
                </div>
            ))}
        </div>
        {!shareView ? (
          <div className="card-buttons">
            {addingTodo ? (
              <AddTodoView baseUrl={baseURL} token={token} class_id={cardItem.class_id} addView={changeAddingStatus} onUpdate={getAllTodos} />
            ) : (
              <TodoCardOptions baseUrl={baseURL} token={token} class_id={cardItem.class_id} addView={changeAddingStatus} getAllClasses={getAllClasses} />
            )}
          </div>
        ) : (
          <div className="card-options">
            <p className="second-header flex-center">Admin</p>
          </div>
        )}
      </div>
    );
};


const AddTodoView = ({ baseUrl, token, class_id, onUpdate, addView}) => {
    const [todoContent, setTodoContent] = useState("");

    const handleAddClick = async () => {
        if(todoContent.trim() !== ""){
            await addTodo(baseUrl, token, class_id, todoContent);
            onUpdate();
            setTodoContent("");
        }
    };

    const handleKeyDown = (e) => {
        if (e.key === 'Enter') {
          e.preventDefault();
          handleAddClick();
        }
    };

    return (
      <div className="full-container add-todo-container">
        <input
          type="text"
          className="add-todo-input"
          value={todoContent}
          onChange={(e) => setTodoContent(e.target.value)}
          placeholder="ToDo?"
          onKeyDown={handleKeyDown}
        />
        <div className="add-todo-buttons full-container">
            <button
            onClick={handleAddClick}
            className="add-todo-button"
            >
            <ion-icon name="add-outline"></ion-icon>
            </button>
            <button
            onClick={addView}
            className="add-todo-button"
            >
            <ion-icon name="arrow-back-outline"></ion-icon>
            </button>
        </div>
      </div>
    );
  };


const TodoCardOptions = ({ baseUrl, token, class_id, addView, getAllClasses }) =>  {
    const { activeClassroom } = useActiveClassroom();
    const {activeShareClass, setShareClass} = useShareContext();
    const [isOnShare, setIsOnShare] = useState(false);

    useEffect(() => {
        setIsOnShare(activeShareClass === class_id);
    }, [activeShareClass, class_id]);

    const handleDeleteButton = async () => {
        const shared_id = await getSharedClass(baseUrl, token, activeClassroom.classroom_name);
        if (shared_id === class_id) {
            await setSharedClass(baseUrl, token, activeClassroom.classroom_name, null);
            setIsOnShare(false); 
        }
        await deleteClass(baseUrl, token, class_id);
        getAllClasses();
    };

    const setOnShare = async () => {
        setIsOnShare(prevIsOnShare => {
            if (prevIsOnShare) {
                setSharedClass(baseUrl, token, activeClassroom.classroom_name, null);
                setShareClass(null);
                return false;
            } else {
                setSharedClass(baseUrl, token, activeClassroom.classroom_name, class_id);
                setShareClass(class_id);
                return true;
            }
        });
    };


    return (
        <div className="full-container flex-inline">
            <div className="todo-card-option full-container flex-center" onClick={addView}>
                <ion-icon name="add-outline"></ion-icon>
            </div>
            <div className={`todo-card-option full-container flex-center ${isOnShare ? "make-green" : null}`} 
                onClick={setOnShare}>
                <ion-icon name="desktop"></ion-icon>
            </div>
            <div className="todo-card-option full-container flex-center" onClick={handleDeleteButton}>
                <ion-icon name="trash"></ion-icon>
            </div>
        </div>
    );
}

const SingleTodo = ({todo, onUpdate}) => {
    const [content, setContent ] = useState(todo.content);
    const [answer, setAnswer] = useState(''); 
    const [isAnswering, setIsAnswering] = useState(false);
    const [isEditing, setIsEditing] = useState(false);

    const baseURL = useBaseUrl();
    const token = window.localStorage.getItem("token");

    useEffect(() => {
        const fetchOldAnswers = async () => {
          try {
            const oldAnswer = await getAnswer(baseURL, todo.todo_id);
            setAnswer(oldAnswer.length !== 0 ? oldAnswer.answer : '');

          } catch (error) {
            console.error('Error fetching old answers:', error);
          }
        };

        if (isAnswering) {
          fetchOldAnswers();
        }
      }, [isAnswering, baseURL, todo.todo_id]);

      const handleUpdate = async (updateType) => {
          try {
              switch (updateType) {
                  case "flag":
                      await updateTodoFlag(baseURL, token, todo.todo_id, !todo.flagged);
                      break;
                  case "complete":
                      await updateTodoComplete(baseURL, token, todo.todo_id, !todo.completed);
                      break;
                  case "content":
                      await updateTodoContent(baseURL, token, todo.todo_id, content);
                      break;
                  case "delete":
                      await deleteTodo(baseURL, token, todo.todo_id);
                      break;
                  case "answer":
                      await handleAnswer(baseURL, token, todo.todo_id, answer);
                      break;
                  default:
                      break;
              }
              onUpdate();
          } catch (error) {
              console.error('Error updating todos:', error);
          }
      }

    const handleEdit = () => {
        setIsEditing(true);
    };    

    const handleQuestion = () => {
        setIsAnswering(true);
    };

    const handleContentUpdate = () => {
        if (content.trim().length === 0) {
        handleUpdate("delete");
        } else {
        handleUpdate("content");
        }
        setIsEditing(false);
    };

    const handleAnswerUpdate = () => {
        handleUpdate("answer");
        setIsAnswering(false);
    };

    return (
        <div className="single-todo-container">
            { isEditing ?
            (<input 
            className="todo-input"
            placeholder={todo.content}
            onChange={(e) => setContent(e.target.value)}
            onBlur={handleContentUpdate}
            onKeyDown={(e) => e.key === "Enter" && handleContentUpdate()}
            autoFocus
            />) :
            (
                <>
                {isAnswering ? (
                    <input 
                    className="todo-input"
                    value={answer}
                    placeholder={todo.content}
                    onChange={(e) => setAnswer(e.target.value)}
                    onBlur={handleAnswerUpdate}
                    onKeyDown={(e) => e.key === "Enter" && handleAnswerUpdate()}
                    autoFocus
                    />
                ) 
                : (
                    <>
                    <div className="single-todo-content flex-inline scroll">
                        <p className="position">{todo.position}.</p>
                        <p className="scroll">{todo.content}</p>
                    </div>
                    <div className={`single-todo-menu flex-center`}>
                        {todo.flagged ? (
                            <button
                            className="menu-button"
                            onClick={() => handleUpdate("flag")}
                            >
                            <ion-icon name="flag"></ion-icon>
                            </button>
                        ) : (
                            <button
                            className="menu-button"
                            onClick={() => handleUpdate("flag")}
                            >
                            <ion-icon name="flag-outline"></ion-icon>
                            </button>
                        )}
                        <button onClick={handleEdit}
                            className="menu-button"
                        >
                            <ion-icon name="create-outline"></ion-icon>
                        </button>
                        <button onClick={handleQuestion}
                            className="menu-button"
                        >
                            <ion-icon name="help-outline"></ion-icon>
                        </button>
                        <button
                            className="menu-button"
                            onClick={() => handleUpdate("delete")}
                        >
                            <ion-icon name="trash-outline"></ion-icon>
                        </button>
                    </div>
                    </>
                )}
                </>
            )}
        </div>       
    );
}

export default TodoCard;
