import { useState, useEffect } from 'react';

import { useBaseUrl } from '../../utils/providers/urlprovider';
import { getActiveClassroom } from '../../services/admin/classroom';

import Sidebar from './SidebarAdmin';
import Mainview from './Mainview';
import FirstClassroom from './FirstClassroom';

import '../../styles/admin/views.css';
import '../../styles/admin/sidebar.css';
import '../../styles/admin/navbar.css';


const AdminHome = ({handleAdminLogout}) => {
    const [ classroomExists, setClasroomExists ] = useState(false);
    const [ activeClassroom, setActiveClassroom ] = useState({});
    const [ classes, setClasses ] = useState([]);
    const [ students, setStudents ] = useState([]);
    const [ todoView, setTodoView ] = useState(true);

    const toggleView = () => {setTodoView(!todoView)}

    const baseURL = useBaseUrl();
    const token = window.localStorage.getItem("token");
    const username = window.localStorage.getItem("username");
    const isAdmin = window.localStorage.getItem("admin");


    const fetchActiveClassroom = async () => {
        try {
            const result = await getActiveClassroom(baseURL, token, username);
            if(result) {
                setClasroomExists(true);
                setActiveClassroom(result);
            }
          } catch (error) {
            console.error('Error fetching active classroom:', error);
          }
    }

    useEffect(() => {
        fetchActiveClassroom();
    }, [activeClassroom]);



    return (
        <div className='home-container full-container'>
             <Mainview todoView={todoView}/>
                    <Sidebar toggleView={toggleView} todoView={todoView} handleAdminLogout={handleAdminLogout}/>
        </div>
    );
}

export default AdminHome;
