import { useState, useEffect } from 'react';
import { BaseUrlProvider } from './utils/providers/urlprovider';
import { ActiveClassroomProvider } from './utils/providers/activeclassroomprovider';
import './styles/Main.css';	
import './styles/color.css'

import { logout, studentLogout } from './utils/authentication/logout';

import isAuthenticated from './utils/authentication/adminauth';

import ChooseLogin from './components/userlogin/ChooseLogin';
import AdminHome from './components/admin/AdminHome';


function App() {
   const [isLoggedIn, setIsLoggedIn] = useState(false);
   const [isAdmin, setIsAdmin] = useState(false);

   const handleLoginState = () => {setIsLoggedIn(!isLoggedIn)}

   const handleAdminLogout = () => {
      logout();
      setIsAdmin(false);
      setIsLoggedIn(false)
   }

   useEffect(() => {
      if (isAuthenticated()) {
         setIsLoggedIn(true);
      }
   }, [])

   return (
      <BaseUrlProvider baseUrl={"http://localhost:8888"}>
         <div className='full-container'>
            {!isLoggedIn ? <ChooseLogin handleLoginState={handleLoginState}/> 
            : 
            ( <AdminHome handleAdminLogout={handleAdminLogout}/>)}
         </div>
      </BaseUrlProvider>

   );
}



export default App;
