import { useState } from 'react'

import '../../styles/login/login.css'

import StudentLogin from './loginForms';

const ChooseLoginButton = ({name, handleHovering, admin=false}) => {
    const [isAdmin] = useState(admin);

    return (
        <div className={`choose-login-button 
        flex-center ${isAdmin ? 'primary-color' 
        : 'secondary-color'}`}
        onMouseEnter={handleHovering}>
            <h1 className='header'>{name}</h1>
        </div>
    )
}

const LeftContainer = () => {
    const [isClicked, setIsClicked] = useState(false);
    const [isHovering, setIsHovering] = useState(false);

    const handleClicked = () => setIsClicked(!isClicked);


    const handleHovering = () => setIsHovering(!isHovering);

    return (
        <div className='half-login-container left-login-container relative'>
            {!isHovering ?
                <ChooseLoginButton name="Member"/>
                : 
                <div className='flex-center full-container'>
                    <StudentLogin />
                </div>
            }
        </div>
    )
}

const RightContainer = () => {
    return (
        <div className='half-login-container right-login-container relative'>
            <ChooseLoginButton name="Admin" admin={true}/>
        </div>
    )
}

const ChooseLogin = () => {
    return (
        <div className='full-container login-container'>
            <LeftContainer />
            <RightContainer />
        </div>
    )

}

export default ChooseLogin
