import Navbar from './Navbar';
import Todoview from './Todoview';
import Groupview from './Groupview';


const Mainview = ({todoView}) => {
    const makeLarger = () =>  {
        if(sizeNumber < 4) {
            const newSize = sizeNumber + 1;
            setSizeNumber(newSize);
            setRowSize(ysize[newSize]);
            setWidthSize(xsize[newSize]);
        }
      }

      const makeSmaller = () => {
          if(sizeNumber > 0) {
              const newSize = sizeNumber - 1;
              setSizeNumber(newSize);
              setRowSize(ysize[newSize]);
              setWidthSize(xsize[newSize])
          }
      }

      const handleResize = (size) => {
          if (size === 'smaller') {
              makeSmaller();
          } else {
              makeLarger();
          }
      }

    return (
        <div className="full-container relative">
            <Navbar viewStatus={todoView}/>
            {todoView ? <Todoview /> : <Groupview />}
        </div>
    );
}

export default Mainview;
