import { useEffect } from "react"



const Message = ({message, isError, clearMessage}) => {

    useEffect(() => {
        const timeoutId = setTimeout(() => {
            clearMessage();
        }, 5000);

        return () => clearTimeout(timeoutId);
    }, [message, clearMessage]);


    if (!message) {
        return null;
    }

    return (
        <div className={`messageContainer`}>
            {message}
        </div>
    );
}


export default Message;
