import { useState, useEffect } from 'react';

import { getClassrooms } from "../../services/admin/classroom";
import { useBaseUrl } from '../../utils/providers/urlprovider';

const Sidebar = ({todoView, toggleView, 
                handleAdminLogout, activeClassroom, 
                setActiveClassroom}) => {

    const [ classrooms, setClassrooms ] = useState([]);

    const baseURL = useBaseUrl();
    const token = window.localStorage.getItem("token");
    const username = window.localStorage.getItem("username");

    const fetchClassrooms = async () => {
        try {
            const classrooms = await getClassrooms(baseURL, token, username);
            setClassrooms(classrooms);
        } catch (error) {
            console.error('Error fetching active classroom:', error);
        }
    }

    const addTodo = () => {
        console.log("Add todo");
    }

    const showMembers = () => {
        console.log("Members");
    }

    const toggleGroupView = () => {
        console.log("Group view");
        toggleView();
    }

    const showGroups = () => {
        console.log("Show groups");
        if (classrooms.length === 0) {

        }

    }

    const openSettings = () => {
        console.log("Settings");
    }

    const logout = () => {
        console.log("Logout");
        handleAdminLogout();
    }

    return (
        <div className="sidebar-container flex-center-column">
            <div className="sidebar-header-container flex-center full-width">
                <h1 className="header">5B</h1>
            </div>
            <div className="sidebar-content-container full-container">
                <div className="first-options">
                    <SidebarOption text={"Add Todo"} handleAction={addTodo}/>
                    <SidebarOption text={"Members"} handleAction={showMembers}/>
                    <SidebarOption text={todoView ? "Group View" : "ToDo View"} handleAction={toggleGroupView}/>
                    <SidebarOption text={"Groups"} handleAction={showGroups}/>
                </div>
                <div className="second-options flex-center-column">
                    <SidebarOption text={"Settings"} handleAction={openSettings}/>
                    <SidebarOption text={"Logout"} handleAction={logout}/>
                </div>

            </div>

        </div>
    );
}


export const SidebarOption = ({text, handleAction, color='primary-color'}) => {
    return (
        <div className="flex-center full-width">
            <button className={`sidebar-option-button ${color}`} onClick={handleAction}>{text}</button>
        </div>
    );
}

export default Sidebar;
