import React, { useCallback, useEffect } from "react";
import { BaseUrlProvider } from "./utils/baseUrlContext";
import { useActiveRoute } from "./utils/providers/ActiveRouteContext";
import { useNavigate } from "react-router-dom";
import { getStudent } from "./services/student";

import LoginRoutes from "./Routes/loginRoutes";
import AdminRoutes from "./Routes/adminRoutes";
import StudentRoutes from "./Routes/studentRoutes";

import isAuthenticated from "./utils/authentication/adminauth"

import './css/Main.css';
import './css/color.css';

function App() {
  const BASE_FETCH_URL = "https://todo-6fsq.onrender.com";
  const { activeRoute, setActiveRoute } = useActiveRoute();

  const navigate = useNavigate();

  const token = window.localStorage.getItem("token");
  const adminStatus = window.localStorage.getItem("admin");
  const username = window.localStorage.getItem("username");


  const checkName = useCallback(async () => {
    try {
      const user = await getStudent(BASE_FETCH_URL, token, username);
      return user.name === null;
    } catch (error) {
        console.error(error);
        return false;
    }
  }, [BASE_FETCH_URL, token, username]);


  const goToRoute = useCallback( async () => {
    if(activeRoute === "studentHome" ) {
      try {
        const isNameAbsent = await checkName();
        if (!isNameAbsent) {
          navigate("/studentHome");
        } else {
          navigate("/createStudentName");
        }
      } catch (error) {
          console.error(error);
      }
    } else {
      navigate(`/${activeRoute}`);
    }
  }, [navigate, activeRoute, checkName]);

  useEffect(() => {
    if (adminStatus === "true" && isAuthenticated()) {
      setActiveRoute("adminHome");
    } else if (adminStatus === "false" && isStudentAuthenticated()) {
      setActiveRoute("studentHome");
    } else {
      setActiveRoute("chooseLogin");
    }
    //eslint-disable-next-line
  }, [adminStatus, token, isAuthenticated(), isStudentAuthenticated()]);

  useEffect(() => {
    goToRoute();
    //eslint-disable-next-line
  }, [activeRoute]);

  return (
    <BaseUrlProvider baseUrl={BASE_FETCH_URL}>
        <div className="fullContainer centerFlex">
          {activeRoute === "adminHome" ? (
            <AdminRoutes />
          ) : activeRoute === "studentHome" ? (
            <StudentRoutes />
          ) : (
            <LoginRoutes />
          )}
        </div>
    </BaseUrlProvider>
  );
}

export default App;

